targets.expr:
(
  avg(
  max_over_time(
    DCGM_FI_DEV_GPU_UTIL{cluster="$cluster", node=~"${node}", gpu=~"${gpu}"}[29s]
    )
  ) 
  unless 
  absent(max_over_time(DCGM_FI_DEV_MIG_MODE{cluster="$cluster", node=~"$node"} == 0)[29s])
)
or 
(
  avg(
    max_over_time(
      DCGM_FI_PROF_GR_ENGINE_ACTIVE{cluster="$cluster", node=~"${node}", gpu=~"${gpu}"}[29s] * 100
    )
  ) 
  unless 
  absent(max_over_time(DCGM_FI_DEV_MIG_MODE{cluster="$cluster", node=~"$node"} == 1)[29s])
)

(
  (
    DCGM_FI_DEV_GPU_UTIL{cluster="$cluster", node=~"${node}", gpu=~"${gpu}"}
    )
  unless 
  absent(max_over_time(DCGM_FI_DEV_MIG_MODE{cluster="$cluster", node=~"$node"} == 0))
)
or 
(
    (
      DCGM_FI_PROF_GR_ENGINE_ACTIVE{cluster="$cluster", node=~"${node}", gpu=~"${gpu}"} * 100
    )
  unless 
  absent(max_over_time(DCGM_FI_DEV_MIG_MODE{cluster="$cluster", node=~"$node"} == 1))
)
