targets.expr:
(
  avg(
  max_over_time(
    DCGM_FI_DEV_GPU_UTIL{cluster="$cluster", UUID=~"(${gpu}|${vgpu})" ,GPU_I_ID=~"${gpu_i_id}"}
    )
  ) by (gpu,UUID)
  unless 
  absent(max_over_time(DCGM_FI_DEV_MIG_MODE{cluster="$cluster", UUID=~"(${gpu}|${vgpu})"} == 0)[29s])
)
or 
(
  avg(
    max_over_time(
      DCGM_FI_PROF_GR_ENGINE_ACTIVE{cluster="$cluster", UUID=~"(${gpu}|${vgpu})" ,GPU_I_ID=~"${gpu_i_id}"} * 100
    )
  ) by(gpu,GPU_I_PROFILE,GPU_I_ID,UUID)
  unless 
  absent(max_over_time(DCGM_FI_DEV_MIG_MODE{cluster="$cluster", UUID=~"(${gpu}|${vgpu})"} == 1)[29s])
)
